module.exports = {
  catchError: function(e) {
    console.log("Error thrown with fullMessage : " + e.message);
    console.log(e);
    console.trace(e);
    return {
      headers: {
        'Content-Type': 'application/json'
      },
      statusCode: e.statusCode || 500,
      body: {
        error: true,
        message: e.myError ? e.message : "An error occurred",
        fullMessage: e.message,
      }
    };
  }
}
